SELECT ew.employee_code, e.employee_name, d.department_name, dt.duty_name, ew.employee_resignation_date, ew.employee_join_date, ehi.RESIDENT_RESISTRATION_NUMBER, ew.out_reason_code, ore.out_reason_name, TRUNC(MONTHS_BETWEEN(SYSDATE,employee_join_date)/12)||'년'||TRUNC(mod(MONTHS_BETWEEN(SYSDATE,employee_join_date),12))||'개월' ||ROUND(MOD(MOD(MONTHS_BETWEEN(SYSDATE, employee_join_date)/12, 1)*12, 1)*(365/12),0)||'일' "WORKING_YEARS"
FROM employee_work ew, employee e, duty dt, department d, employee_hr_info ehi, site s, job_class jc, position p, job_dis jd, work_type wt, pay_type pt, out_reason ore
WHERE ew.employee_code=e.employee_code(+) AND ew.employee_code = ehi.employee_code(+) AND ew.duty_code = dt.duty_code(+) AND ew.out_reason_code = ore.out_reason_code(+) AND ew.department_code =d.department_code(+) AND d.site_code=s.site_code(+) AND ew.job_class_code = jc.job_class_code(+) AND ew.position_code = p.position_code(+) AND ew.duty_code = dt.duty_code(+) AND ew.job_dis_code = jd.job_dis_code(+) AND ew.work_type_code = wt.work_type_code(+) AND ew.pay_type_code = pt.pay_type_code(+) AND ROUND(MOD(MOD(MONTHS_BETWEEN(SYSDATE,employee_join_date)/12, 1)*12, 1)*(365/12),0) > '0'
AND s.site_name = #{site}
AND d.department_name = #{department}
AND jc.job_class_name = #{job_class}
AND p.position_name = #{position}
AND dt.duty_name = #{duty}
AND jd.job_dis_name = #{job_dis}
AND wt.work_type = #{work_type}
AND pt.pay_type_name = #{pay_type}
AND TO_DATE(ew.employee_resignation_date,'YYYY-MM-DD') BETWEEN #{date} AND #{date2}
and d.department_code = #{Sdepartment_code}
and e.employee_code = #{Semployee_code}
