mapper.select:
select SUBSTR(TO_DATE(SUBSTR(y.payment_date,1,6), 'YYYY-MM'),1,5) as payment_DATE1, y.payment_date as payment_DATE2, z.payment_code, yg.payment_des_name, c.department_code as DEPARTMENT_CODE, c.department_name as DEPARTMENT_NAME, a.employee_code as EMPLOYEE_CODE, a.employee_name as EMPLOYEE_NAME, z."basic_SALARY" as BASIC_SALARY,z."position_SALARY" as POSITION_SALARY , z."bonus_SALARY" as BONUS_SALARY from employee a,(select x.payment_code, x.employee_code, sum(case when x.PAYMENT_RECEIPT_ITEM='기본급' then x.PAYMENT_RECEIPT_PRICE end) as "basic_SALARY", sum(case when x.PAYMENT_RECEIPT_ITEM='직책수당' then x.PAYMENT_RECEIPT_PRICE end) as "position_SALARY", sum(case when x.PAYMENT_RECEIPT_ITEM='상여급' then x.PAYMENT_RECEIPT_PRICE end) as "bonus_SALARY" from payment_receipt x
where x.payment_code = (select max(g.payment_code) from payment_receipt g)
where x.payment_code = #{Ppayment_code}
group by employee_code, payment_code) z, payment y, payment_des yg, department c, employee_work d, site o where a.employee_code = z.employee_code and c.department_code=d.department_code and d.employee_code=a.employee_code and o.site_code = c.site_code and z.payment_code=y.payment_code and yg.payment_des_code= y.payment_des_code
and o.site_name = #{searchSite}
and c.department_name = #{DeptList}
order by c.DEPARTMENT_CODE
