mapper.select:
select x.payment_code,(select substr(payment_date,0,6) from payment z where x.payment_code= z.payment_code) as "payment_DATE", count(distinct x.employee_code) as "employee_COUNT", sum(case when x.PAYMENT_RECEIPT_ITEM='기본급' then x.PAYMENT_RECEIPT_PRICE end) as "basic_SALARY", sum(case when x.PAYMENT_RECEIPT_ITEM='직책수당' then x.PAYMENT_RECEIPT_PRICE end) as "position_SALARY", sum(case when x.PAYMENT_RECEIPT_ITEM='상여급' then x.PAYMENT_RECEIPT_PRICE end) as "bonus_SALARY" from payment_receipt x,department c, employee_work d, site o where x.payment_code in (select y.payment_code from payment y where substr(to_date(y.payment_date,'yyyy/mm/dd'),1,5) between #{searchDate1} and #{searchDate2} ) and c.department_code=d.department_code and d.employee_code=x.employee_code and o.site_code = c.site_code
and o.site_name = #{searchSite}
and c.department_name = #{DeptList}
group by x.payment_code

select c.department_code as DEPARTMENT_CODE, c.department_name as DEPARTMENT_NAME, a.employee_code as EMPLOYEE_CODE, a.employee_name as EMPLOYEE_NAME, z."total_basic_SALARY" as total_BASIC_SALARY,z."total_position_SALARY" as total_POSITION_SALARY , z."total_bonus_SALARY" as total_BONUS_SALARY, t."basic_SALARY" as BASIC_SALARY,t."position_SALARY" as POSITION_SALARY , t."bonus_SALARY" as BONUS_SALARY from employee a, (select x.employee_code, sum(case when x.PAYMENT_RECEIPT_ITEM='기본급' then x.PAYMENT_RECEIPT_PRICE end) as "basic_SALARY", sum(case when x.PAYMENT_RECEIPT_ITEM='직책수당' then x.PAYMENT_RECEIPT_PRICE end) as "position_SALARY", sum(case when x.PAYMENT_RECEIPT_ITEM='상여급' then x.PAYMENT_RECEIPT_PRICE end) as "bonus_SALARY" from payment_receipt x where x.payment_code = #{payment_CODE}
group by employee_code) t,
(select x.employee_code, sum(case when x.PAYMENT_RECEIPT_ITEM='기본급' then x.PAYMENT_RECEIPT_PRICE end) as "total_basic_SALARY", sum(case when x.PAYMENT_RECEIPT_ITEM='직책수당' then x.PAYMENT_RECEIPT_PRICE end) as "total_position_SALARY", sum(case when x.PAYMENT_RECEIPT_ITEM='상여급' then x.PAYMENT_RECEIPT_PRICE end) as "total_bonus_SALARY" from payment_receipt x,payment p
where x.payment_code = p.payment_code and substr(to_date(p.payment_date,'yyyy/mm/dd'),1,2) in substr(to_date(CONCAT(#{payment_DATE},'01') ,'YYYYmmdd'),1,2) group by employee_code) z, department c, employee_work d, site o where a.employee_code = z.employee_code and c.department_code=d.department_code and d.employee_code=a.employee_code and o.site_code = c.site_code and t.employee_code = z.employee_code
and o.site_name = #{searchSite}
and c.department_name = #{DeptList}

select x.payment_code,(select substr(payment_date,0,6) from payment z where x.payment_code= z.payment_code) as "payment_DATE", sum(case when x.PAYMENT_RECEIPT_ITEM='기본급' then x.PAYMENT_RECEIPT_PRICE end) as "basic_SALARY", sum(case when x.PAYMENT_RECEIPT_ITEM='직책수당' then x.PAYMENT_RECEIPT_PRICE end) as "position_SALARY", sum(case when x.PAYMENT_RECEIPT_ITEM='상여급' then x.PAYMENT_RECEIPT_PRICE end) as "bonus_SALARY", sum(x.PAYMENT_RECEIPT_PRICE) as "total_SALARY" from payment_receipt x,department c, employee_work d, site o where x.payment_code in (select y.payment_code from payment y where to_date(y.payment_date,'yyyymmdd') between add_months(CONCAT(#{payment_DATE},'01'),-6) and add_months(CONCAT(#{payment_DATE},'01'),6) ) and c.department_code=d.department_code and d.employee_code=x.employee_code and o.site_code = c.site_code
and x.employee_code = #{employee_CODE}
group by x.payment_code
